import React from 'react'

class App extends React.Component {
  constructor () {
    super()
    this.state = {
      movie: movie,
      title: '',
      rating:'',
      recommend: ''
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
    this.deleteMovie = this.deleteMovie.bind(this)
  }
  deleteMovie(index) {
    this.setState({
      movie:{
        songs: [
        ...this.state.playlist.songs.slice(0, index),
        ...this.state.playlist.songs.slice(index + 1)
        ],
        created: this.state.playlist.created,
        title: this.state.playlist.title
      }
    })
  }
  handleChange (event) {
    this.setState({[event.target.id]: event.target.value})
  }
  handleSubmit(event){
    event.preventDefault()
    const newSong = {
      artist: this.state.artist,
      title: this.state.title,
      time: this.state.time
    }
    // BAD!
    // DO NOT DO THIS
    // newSongArray = this.state.playlist.songs.push(newSong)
    const newSongArray  = [ newSong, ...this.state.playlist.songs]
    this.setState({
      playlist: {
        created: this.state.playlist.created,
        title: this.state.playlist.title,
        songs: newSongArray,
        title:'',
        time:0,
        artist:''
      }
    })
  }
  render () {
    return (
      <div className="tunr-container">
        <header>
          <h1>Tunr.</h1>
          <h2>For all your playlist needs</h2>
        </header>
        <Playlist playlist={this.state.playlist}
        handleAdd={this.addLovedSong}
        handleDelete={this.deleteSong}/>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor='title'>title</label>
          <input
            type='text'
            value={this.state.title}
            id='title'
            onChange={this.handleChange}
          />
          <label htmlFor='artist'>artist</label>
          <input
            type='text'
            value={this.state.artist}
            id='artist'
            onChange={this.handleChange}
          />
          <label htmlFor='time'>time</label>
          <input
            type='text'
            value={this.state.time}
            id='time'
            onChange={this.handleChange}
          />
          <input type='submit' />
        </form>
      </div>
    )
  }
}

export default App
